mapper.insert:
INSERT INTO unidad (
    marca, modelo, anio, VIN, estatus, idTipoUnidad
) VALUES (
    #{marca}, #{modelo}, #{anio}, #{vin}, 'activa', #{idTipoUnidad}
)

mapper.update:
UPDATE unidad SET
    marca = #{marca},
    modelo = #{modelo},
    anio = #{anio},
    idTipoUnidad = #{idTipoUnidad}
WHERE idUnidad = #{idUnidad}
